mapper.select:
		SELECT COUNT(*)
		FROM (SELECT S.*, 
            (SELECT T.LNO AS LNO FROM TOWN T WHERE T.TNO = S.TNO) LNO,
            (SELECT SUB.KNO AS KNO FROM SUBJECT SUB WHERE SUB.SUBNO = S.SUBNO) KNO,
            (SELECT M.MNAME AS MNAME FROM MEMBER M WHERE M.MNO = S.MNO) MNAME
        FROM STUDY S 
        WHERE TYPE = 'lecture') A
        WHERE 1=1
		
			AND A.LNO = #{lno}
		
		
			AND A.KNO = #{kno}
		
		
			AND A.TNO = #{tno}
		
		
			AND A.DNO = #{dno}
		
		
			AND A.SUBNO = #{subno}
		
		
			AND A.MNAME = #{leadername}
		
		ORDER BY SNO DESC

SELECT SNO, (SELECT M.MNAME AS MNAME FROM MEMBER M WHERE M.MNO = A.MNO) MNAME, (SELECT L.LOCAL AS LOCAL FROM LOCAL L WHERE L.LNO = A.LNO) LOCAL, (SELECT T.TOWNNAME AS TNAME FROM TOWN T WHERE T.TNO = A.TNO) TNAME, (SELECT K.KINDNAME AS KNAME FROM KIND K WHERE K.KNO = A.KNO) KNAME, (SELECT SUB.SUBJECTNAME AS SUBNAME FROM SUBJECT SUB WHERE SUB.SUBNO = A.SUBNO) SUBNAME, (SELECT D.DIFFICULTNAME AS DNAME FROM DIFF D WHERE D.DNO = A.DNO) DNAME, (SELECT M.COVER AS COVER FROM MEMBER M WHERE M.MNO = A.MNO) COVER, (SELECT M.MPROFILE AS PROFILE FROM MEMBER M WHERE M.MNO = A.MNO ) PROFILE, TITLE, UPFILE, TIME, NVL(PRICE, 0) AS PRICE, STATUS, TO_CHAR(LDATE,'yyyy-mm-dd') LDATE, TO_CHAR(SDATE,'yyyy-mm-dd') SDATE, TO_CHAR(EDATE,'yyyy-mm-dd') EDATE, TO_CHAR(REGDATE,'yyyy-mm-dd') REGDATE FROM (SELECT S.*, (SELECT T.LNO AS LNO FROM TOWN T WHERE T.TNO = S.TNO) LNO, (SELECT SUB.KNO AS KNO FROM SUBJECT SUB WHERE SUB.SUBNO = S.SUBNO) KNO, (SELECT M.MNAME AS MNAME FROM MEMBER M WHERE M.MNO = S.MNO) MNAME FROM STUDY S WHERE TYPE = 'lecture') A WHERE 1=1
AND A.LNO = #{lno}
AND A.KNO = #{kno}
AND A.TNO = #{tno}
AND A.DNO = #{dno}
AND A.SUBNO = #{subno}
AND A.MNAME = #{leadername}
ORDER BY SNO DESC
