mapper.select:
SELECT COUNT(*) TOTALCOUNT FROM REPLY


	AND BNO = #{bno}		

	AND STATUS ='Y'

SELECT R.*, M.*, B.*, TO_CHAR(R.REGDATE, 'YYYY-MM-DD') REGS FROM REPLY R INNER JOIN BOARD B ON R.BNO = B.BNO INNER JOIN MEMBER M ON R.MNO = M.MNO
AND type = #{type}
AND R.BNO = #{bno}
AND R.STATUS ='Y'
START WITH R.LEV = 1 CONNECT BY PRIOR R.RNO = R.PARENTNO ORDER SIBLINGS BY R.REGDATE DESC
